mapper.select:
SELECT * FROM ${sfaas.db.schema}.factory f JOIN ${sfaas.db.schema}.welder w ON f.factory_id = w.factory_id WHERE w.factory_id = #{factoryId}
AND (create_at BETWEEN #{startAt} AND #{endAt})
AND status = #{status}
ORDER BY create_at DESC LIMIT 100

SELECT speed, SUM(status) AS total_count, SUM(CASE WHEN status = 'OK' THEN 1 ELSE 0 END) AS ok_count, SUM(CASE WHEN status = 'NG' THEN 1 ELSE 0 END) AS ng_count FROM ${sfaas.db.schema}.user u JOIN ${sfaas.db.schema}.welder w ON u.factory_id = w.factory_id WHERE u.factory_id = #{factoryId}
AND (create_at BETWEEN #{startAt} AND #{endAt})
GROUP BY speed
